select D.Barcode, R.DocumentNumber as [Belge No], R.DocumentDate as [Ýade Tarihi], C.Name as [Cari], SRR.Name as [Ýade Nedeni], D.Note as [Açýklama], PT.Name as [Ürün Tipi], PK.Name as [Ürün Cinsi], D.Quantity as [Ýade Gelen Miktar], (select isnull(sum(Quantity), 0) from ReadResourceMovement where GCRecord is null and ProductionBarcode like 'M%' and ReadResource in (select Oid from ReadResource where GCRecord is null and Barcode = D.Barcode)) as [Deðerlendirme Miktarý] , (select isnull(sum(Quantity), 0) from ReadResourceMovement where GCRecord is null and ProductionBarcode like 'F%' and ReadResource in (select Oid from ReadResource where GCRecord is null and Barcode = D.Barcode)) as [Fire Miktarý] , (select isnull(sum(Quantity), 0) from Store where GCRecord is null and Barcode = D.Barcode) as [Depo Miktarý] from SalesReturnDetail D inner join SalesReturn R on R.Oid = D.SalesReturn inner join Contact C on C.Oid = R.Contact inner join SalesReturnReason SRR on SRR.Oid = D.SalesReturnReason inner join Product P on P.Oid = D.Product inner join ProductType PT on PT.Oid = P.ProductType inner join ProductKind PK on PK.Oid = P.ProductKind where D.GCRecord is null 